/*blog_user*/
CREATE TABLE `blog_user` (
                           `id` INT(11) NOT NULL AUTO_INCREMENT,
                           `username` VARCHAR(255) NOT NULL COLLATE 'utf8mb4_unicode_ci',
                           `password` VARCHAR(255) NOT NULL COLLATE 'utf8mb4_unicode_ci',
                           PRIMARY KEY (`id`)
);


/*blog_categories*/
CREATE TABLE `blog_categories` (
                                 `id` INT(11) NOT NULL AUTO_INCREMENT,
                                 `name` VARCHAR(255) NOT NULL COLLATE 'utf8mb4_unicode_ci',
                                 `img` VARCHAR(255) NOT NULL COLLATE 'utf8mb4_unicode_ci',
                                 PRIMARY KEY (`id`)
);


/*blog_content*/
CREATE TABLE `blog_content` (
                              `id` INT(11) NOT NULL AUTO_INCREMENT,
                              `id_categorie_id` INT(11) NOT NULL,
                              `status` TINYINT(1) NOT NULL,
                              `created_at` DATETIME NOT NULL,
                              `title` VARCHAR(255) NOT NULL,
                              `content` LONGTEXT NOT NULL,
                              `author` VARCHAR(50) NOT NULL,
                              PRIMARY KEY (`id`),
                              INDEX `IDX_12338D2A9F34925F` (`id_categorie_id`),
                              CONSTRAINT `FK_12338D2A9F34925F` FOREIGN KEY (`id_categorie_id`) REFERENCES `blog_categories` (`id`)
);

